    guardarDeposito() {
      const r = this.renta;
      if (!r) return;
      if (!this.soyDueno) return;
      if (this.estadoNormalizado !== 'finalizada') return;
      const id = r.id_renta ?? r.id;
      if (typeof id !== 'number') return;
      const total = this.deposito;
      if (!(total > 0)) {
        this.depositoError = 'Esta renta no tiene depósito.';
        return;
      }
      const motivo = (this.depositoMotivoControl.value ?? '').toString().trim();
      const nota = (this.depositoNotaControl.value ?? '').toString().trim();
      const montoRaw = this.depositoMontoRetenidoControl.value;
      const monto = typeof montoRaw === 'number' && Number.isFinite(montoRaw) ? montoRaw : 0;
      if (!motivo) {
