        return;
      }
      if (!nota) {
        this.depositoError = 'Indica una nota.';
        return;
      }
      if (!(monto >= 0 && monto <= total)) {
        this.depositoError = `El monto retenido debe estar entre 0 y ${total}.`;
        return;
      }
      if (monto > 0) {
